double int_simpson(double from, double to, double n, double m, double c)
{
   double h = (to - from) / n;
   double sum1 = 0.0;
   double sum2 = 0.0;
   int i;

   for(i = 0;i < n;i++)
	   //return eqn
      sum1 += m*(from + h * i + h / 2.0) + c;

   for(i = 1;i < n;i++)
      sum2 += m * (from + h * i) + c;

   return h / 6.0 * ((m*(from)+c) + (m*(to)+c) + 4.0 * sum1 + 2.0 * sum2);
}


